    let flag = false;
    const [changed,setChanged] = useState("")
    const showList = props.list.map((p) =>
    p.status === false ?
    <li key={p.id}>
        {p.txt}
        <button 
        onClick={() => props.onRemove(p.id)}>
            X
        </button>
        <button
        onClick = {() => props.onEdit(p.id)}>
            Edit
        </button>
    </li>
    :
    <Edit/>
    )

    return (
    <ul>
    {showList}
    </ul>)
}
